mapper.update:
update t_succulent
set pname = #{pname},alias = #{alias},florescence = #{florescence},classification = #{classification},
intro = #{intro},feature = #{feature},habits = #{habits}
where plantid = #{plantid}

mapper.insert:
insert into t_search (word, num)
values (#{word}, 1)
ON DUPLICATE KEY UPDATE num = num + 1;
